import React, { Component, useEffect , useState } from 'react'
import './Single.css'
import {
  BrowserRouter as Router,
  useParams,

} from "react-router-dom";
import Cast from './Cast'
import Related from './Related'
const axios = require('axios');

function Single(){

  let { id } = useParams();

  const [data, setData] = useState({
    genres:[]
  });

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.themoviedb.org/3/movie/${id}?api_key=3fe0146e60722e9fc1e2731f6e6bea06&language=en-US`,
      );

      setData(result.data);
    };

    fetchData();
  }, []);

    return (

      <div className="single__view-panel">

          <div className="hero">
            <div className="single__view-gradient"></div>
            <div className="single__view-content">
                <h1>{data.title}</h1>
                <h4>{data.tagline}</h4>

                <ul class="genres">
                      {data.genres.map( (genre, key) =>
                        <li key={key}>{genre.name}</li>
                      )}
                </ul>
                <p>{data.overview}</p>
                <p><a href="#" class="btn btn-play">Play Trailer</a></p>
            </div>
            <img src={"https://image.tmdb.org/t/p/original"+data.backdrop_path} alt={data.title} />
        </div>
        <Cast id={data.id} />
        <Related id={data.id} />
      </div>
    )

}

export default Single
