import React, { Component } from 'react'
const axios = require('axios');
export class Cast extends Component {
    state = {
        credits: {
            casts:[]
        }
}

    componentDidMount(){

      axios.get(`https://api.themoviedb.org/3/movie/${this.props.id}/credits?api_key=3fe0146e60722e9fc1e2731f6e6bea06`)
            .then(response => this.setState({
                credits: response.data
                }))
            .catch(function(err){
                console.log(err)
          })
        }
  render() {
    const {credits} = this.state;

    console.log(credits);
    return (
      <div>
        {this.props.id}
      </div>
    )
  }
}

export default Cast
