import React, { Component, useEffect , useState } from 'react'
import './Single.css'
import {
  BrowserRouter as Router,
  Link,
  useParams,

} from "react-router-dom";
import Cast from './Cast'
import Related from './Related'
const axios = require('axios');

function Single(){

  let { id } = useParams();

  const [data, setData] = useState({
    genres:[]
  });

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.themoviedb.org/3/movie/${id}?api_key=3fe0146e60722e9fc1e2731f6e6bea06&language=en-US`,
      );

      setData(result.data);
    };

    fetchData();
  }, []);

    return (

      <div className="single__view-panel">
          <div className="single__view-gradient"></div>
          <div className="hero"><img src={"https://image.tmdb.org/t/p/original"+data.backdrop_path} alt={data.title} /></div>
          <div className="single__view-content">
              <h1>{data.title}</h1>
              <h4>{data.tagline}</h4>
              <div className="movie__details_row">
                <p className="vote_average">{data.vote_average}</p>
                <p className="runtime">{data.runtime} Minutes</p>
                <p className="release">{data.release_date}</p>
              </div>
              <ul>
                {data.genres.map( (genre, key) =>
                  <li key={key}>{genre.name}</li>
                )}
              </ul>

              <p>{data.overview}</p>

              <p><a href="#" class="btn btn-play">Play Trailer</a></p>

              <Cast id={data.id} />
              <Link to={`/${data.id}`}>
                <Related id={data.id} />
              </Link>

          </div>
      </div>
    )

}

export default Single
