import React, { Component } from 'react';
import Video from './Video';
import TopRated from './TopRated';
import Latest from './Latest';
const axios = require('axios');
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
export class Popular extends Component {

state = {
    movies: {
      results:[]
    },
    videos:{
      results:{}
    }
}
componentDidMount(){

  axios.get('https://api.themoviedb.org/3/movie/popular?api_key=3fe0146e60722e9fc1e2731f6e6bea06')
        .then(response => this.setState({
                movies: response.data
            }))
        .catch(function(err){
            console.log(err)
      })
    }
  clickButton = () =>{
    console.log('clicked');
  }

  render() {

      const {movies} = this.state;
      console.log(movies);
    return (
      <div className="container">
         <button onClick={this.clickButton}>Button Click</button>
          <div className="row">
            <div class="col-md-12"><h2>Popular</h2></div>
              {/*https://image.tmdb.org/t/p/original/ */}
              {this.state.movies.results.map( (movie, id) =>

              <div className="col-md-3" key={movie.id}>
                <Link to={movie.id} />
                  <img src={"https://image.tmdb.org/t/p/w200"+movie.backdrop_path} />
                 {/*<Video id={movie.id} /> */} 
                 <Link />    
              </div>  

          )}

            </div>
            <TopRated />

      </div>

    )
  }
}

export default Popular
