import React, { Component } from 'react'

export class Cast extends Component {
  constructor(props){  
    super(props);  
    this.state = {
      user: null
    } 
    this.handleEvent = this.handleEvent.bind(this);  
  } 

  componentDidMount () {
    const { id } = this.props.match.params

    fetch(`https://api.twitter.com/user/${id}`)
      .then((user) => {
        this.setState(() => ({ user }))
      })
  }

  render() {
    return (
      <div>

      </div>
    )
  }
}

export default Cast
