import React from 'react';

import './App.css';
import Home from './components/Home';
import Single from './components/Single';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
function App() {

  return (
    <Router>
        <div className="App">



      <Switch>
        <Route exact path="/">
        <Home />
      </Route>
      <Route path="/:id" children={<Single/>} />
      </Switch>
    </div>

    </Router>

  );
}

export default App;
