import React, { Component, useEffect , useState } from 'react'
import './Single.css'
import {
  BrowserRouter as Router,
  useParams,

} from "react-router-dom";
import Video from './Video'
const axios = require('axios');

function Single(){

  let { id } = useParams();

  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        `https://api.themoviedb.org/3/movie/${id}?api_key=3fe0146e60722e9fc1e2731f6e6bea06&language=en-US`,
      );

      setData(result.data);
    };

    fetchData();
  }, []);
  console.log(data);

    return (

      <div className="single__view-panel">
          <div className="single__view-gradient"></div>
          <div className="hero"><img src={"https://image.tmdb.org/t/p/original"+data.backdrop_path} alt={data.title} /></div>
          <div className="single__view-content">
              <h1>{data.title}</h1>
              <h4>{data.tagline}</h4>
              {data.genres.map(genre => 
                {genre.name} 
              )}
              <p className="vote_average">{data.vote_average}</p>
              <p className="runtime">{data.runtime}</p>
              <p className="release">{data.release_date}</p>
              {/* <Cast id={data.id} /> */}
              <Video id={data.id} />
              <p><a href="#" class="btn btn-play">Play</a></p>
              <p>{data.overview}</p>
          </div>
      </div>
    )

}

export default Single
