import React, { Component, useEffect , useState } from 'react'
import './Single.css'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams,
  useRouteMatch
} from "react-router-dom";
const axios = require('axios');

function Single(){
 const details = useState({});
  let { id } = useParams();
  useEffect( () => {
    axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=3fe0146e60722e9fc1e2731f6e6bea06&language=en-US`)
            .then(response => this.setState({
                    details: response.data
                }))
            .catch(function(err){
                console.log(err)
          })

    }        
  )


  console.log(details);
    return (

      <div>
           <h3>ID: {id}</h3>
      </div>
    )

}

export default Single
