import React, { Component, useEffect , useState } from 'react'

const axios = require('axios');

function Cast(id){



  const [data, setData] = useState({
      cast:[]
  });

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(        
        `https://api.themoviedb.org/3/movie/${id}/credits?api_key=3fe0146e60722e9fc1e2731f6e6bea06`,
      );

      setData(result.data);
    };

    fetchData();
  }, []);


    return (

      <div>
          {data.cast.map(people => 
            <h4>{people.name}</h4>    
        )}
      </div>
    )

}

export default Cast
