import React, { Component } from 'react'
const axios = require('axios');
import {
  BrowserRouter as Router,
  Link,
  useRouteMatch
} from "react-router-dom";
export class Hero extends Component {
    state = {
        featured: {
          results:[]
        }
    }
    componentDidMount(){

      axios.get('https://api.themoviedb.org/3/movie/popular?api_key=3fe0146e60722e9fc1e2731f6e6bea06&language=en-US&page=44')
            .then(response => this.setState({
                    featured: response.data
                }))
            .catch(function(err){
                console.log(err)
          })
        }
  render() {
    const {featured} = this.state;
    console.log(featured);
    return (
      <div>
        {this.state.featured.results.slice(0, 1).map( (feature, id) =>

              <div className="hero_row" key={feature.id}>

                  <img src={"https://image.tmdb.org/t/p/original"+feature.backdrop_path} />
                    <h1>{feature.title}</h1>
                    <p>{feature.overview}</p>
                    <Link to={`/${feature.id}`}>View</Link>

                {/*<Video id={movie.id} /> */} 

              </div>  

          )}
      </div>
    )
  }
}

export default Hero
