import React, { Component } from 'react'

export class Cast extends Component {

  state = {
    user: null
  }
  componentDidMount () {
    const { id } = this.props.match.params

    fetch(`https://api.twitter.com/user/${id}`)
      .then((user) => {
        this.setState(() => ({ user }))
      })
  }

  render() {
    return (
      <div>

      </div>
    )
  }
}

export default Cast
